import React, { useContext, createRef } from 'react';
import { AppState } from '../context';
import { Grid, Header, Container, Card, Image, Segment, Sticky, Icon } from 'semantic-ui-react';

const HomePage = () => {
  const state = useContext(AppState);
  const { data } = state;
  console.log(data)
  const contextRef = createRef();

  const Games = () => {
    return (
      <Card.Group itemsPerRow={1}>
        {data.map(game => 
          <Card>
            <Card.Content>
              <Card.Header content={game.game} />
              <Card.Meta style={{ fontStyle: "italic" }}>
                {game.minPlayers}-{game.maxPlayers} Players
              </Card.Meta> 
              <Card.Description style={{ color: "grey", fontWeight: "bold", fontSize: "11px" }}>
                {game.lender} {<Icon name="star" />}{game.rating}
              </Card.Description>
            </Card.Content>
            <Image.Group size="tiny">
              <Image src={game.images[0]} />
              <Image src={game.images[1]} />
              <Image src={game.images[2]} />
            </Image.Group>
          </Card>
        )}
      </Card.Group>
    );
  }

  return (
    <div ref={contextRef}>
      <Sticky context={contextRef}>
        <PageHeader />
      </Sticky>
      <Container>
        <Games />
      </Container>
    </div>
  );
};

const PageHeader = () => {
  return (
    <Segment
      basic
      attached='top'
      style={{ backgroundColor: "orange", textAlign: "center" }}
      fluid
    >
      <Header content="Marketplace" size="large" />
    </Segment>
  );
}

export default HomePage;
