import React from 'react';
import { Grid, Image, Button, Header, Label, Container, Divider } from 'semantic-ui-react';

const ListingPage = ({ listing }) => {
  return (
    <Container>
      <Grid>
        <Grid.Row>
          <Image src={listing.images[0]} />
        </Grid.Row>
        <Grid.Row style={{ margin: "0px 10px 0px 10px "}}>
          <Header>
            {listing.game}
            <Header size="small" style={{ fontWeight: "lighter", fontStyle: "italic"}} />
            <Header.Subheader content={listing.description} />
          </Header>
        </Grid.Row>
        <Divider />
        <Grid.Row style={{ margin: "0px 10px 0px 10px "}}>
          <Header size="small">
            Tags: 
            {listing.genre.map(g => <Label content={g} />)}
          </Header>
        </Grid.Row>
      </Grid>
    </Container>
  );
};

export default ListingPage;
