import React, { useContext, createRef } from 'react';
import { AppState } from '../context';
import { Button, Header, Container, Card, Image, Segment, Sticky, Icon, Grid, Input } from 'semantic-ui-react';

const HomePage = () => {
  const state = useContext(AppState);
  const { data } = state;
  console.log(data)
  const contextRef = createRef();

  return (
    <div ref={contextRef}>
      <Sticky context={contextRef}>
        <PageHeader />
      </Sticky>
      <Container>
        <Grid padded>
          <Grid.Row columns={2}>
            <Grid.Column>
              <Input icon='search' iconPosition='left' placeholder="Search..." />
            </Grid.Column>
            <Grid.Column>
              <Button basic content="Filter" />
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Games data={data} />
          </Grid.Row>
        </Grid>
      </Container>
    </div>
  );
};

const PageHeader = () => {
  return (
    <Segment
      basic
      attached='top'
      style={{ backgroundColor: "orange", textAlign: "center" }}
      fluid
    >
      <Header content="Marketplace" size="large" />
    </Segment>
  );
};

const Games = ({ data }) => {
  return (
    <Card.Group itemsPerRow={1}>
      {data.map(game => 
        <Card>
          <Card.Content>
            <Header style={{fontWeight: "lighter", fontSize: "12px"}} floated='right' content={game.distance} />
            <Card.Header content={game.game} />
            <Card.Meta style={{ fontStyle: "italic", fontSize: "13px" }}>
              {game.minPlayers}-{game.maxPlayers} Players
            </Card.Meta> 
            <Card.Description style={{ color: "grey", fontWeight: "bold", fontSize: "12px" }}>
              {game.lender} {<Icon style={{marginRight: "0px", marginLeft: "10px"}} name="star" />}{game.rating}
            </Card.Description>
          </Card.Content>
          <Image.Group size="tiny">
            <Image src={game.images[0]} />
            <Image src={game.images[1]} />
            <Image src={game.images[2]} />
          </Image.Group>
          <Card.Content extra>
            <Button 
              style={{ backgroundColor: "white", color: "orange", border: "1px solid lightgray" }} 
              content="Rent Now" 
              fluid 
            />
          </Card.Content>
        </Card>
      )}
    </Card.Group>
  );
};

export default HomePage;
